  `avg_rev` decimal(10,1) DEFAULT NULL COMMENT '平均月營收(萬)',
  `avg_gp` decimal(10,1) DEFAULT NULL COMMENT '平均月毛利(萬)',
  `avg_gpm` decimal(10,2) DEFAULT NULL COMMENT '平均毛利率',
  `base_num` decimal(10,1) DEFAULT NULL COMMENT '基數',
  `quota` int(10) DEFAULT NULL COMMENT '額度',
  `b_empno` varchar(7) DEFAULT NULL COMMENT '建檔員工編號',
  `b_dept_id` varchar(8) DEFAULT NULL COMMENT '建檔部門代號',
  `b_proc` varchar(30) DEFAULT NULL COMMENT '建檔程式名稱',
  `b_date` datetime DEFAULT NULL COMMENT '建檔日期 (年月日時分)',
  `m_empno` varchar(7) DEFAULT NULL COMMENT '異動員工編號',
  `m_dept_id` varchar(8) DEFAULT NULL COMMENT '異動部門代號',
  `m_proc` varchar(30) DEFAULT NULL COMMENT '異動程式名稱',
  `m_date` datetime DEFAULT NULL COMMENT '異動日期 (年月日時分)',
  `d_empno` varchar(7) DEFAULT NULL COMMENT '作廢員工編號',
  `d_dept_id` varchar(8) DEFAULT NULL COMMENT '作廢部門代號',
  `d_proc` varchar(30) DEFAULT NULL COMMENT '作廢程式名稱',
  `d_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '作廢日期 (年月日時分)',
  `fromip` varchar(15) DEFAULT NULL COMMENT '來源 IP'
) ENGINE=MyISAM DEFAULT CHARSET=big5 COMMENT='贈禮對象規劃表身(公司)';
