  `d_proc` varchar(30) DEFAULT NULL COMMENT '作廢程式名稱',
  `d_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '作廢日期 (年月日時分)',
  `fromip` varchar(15) DEFAULT NULL COMMENT '來源 IP'
) ENGINE=MyISAM DEFAULT CHARSET=big5 COMMENT='贈禮對象規劃表頭';
